INSERT INTO `ecm_article` VALUES ('23', '', '注册新用户', '4', '0', '', '<p><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'宋体\';\">1、新用户注册通道</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第一步：用户直接点击</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'宋体\';\">网页面上方的</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">注册</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">，进入注册页面。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第二步：填写基本注册信息。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第三步：阅读并同意&lt;&lt;</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'宋体\';\">网用户协议&gt;&gt;。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第四步：点击</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">注册</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">按钮。提示：注册成功。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第</span><span style=\"font-family: \'宋体\';\">五</span><span style=\"font-family: \'宋体\';\">步：用户注册成功后</span><span style=\"font-family: \'宋体\';\">可以进入用户中心，编辑个人资料和</span><span style=\"font-family: \'宋体\';\">上传头像；</span><span style=\"font-family: \'宋体\';\">请</span><span style=\"font-family: \'宋体\';\">尽快将您的姓名、性别、所在地等个人资料填写完整噢；</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'宋体\';\">2、</span><span style=\"font-family: \'宋体\';\">找回密码</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第一步：</span><span style=\"font-family: \'宋体\';\">进入登陆页面，点击</span><span style=\"font-family: \'宋体\';\">密码右侧的</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">忘记密码</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第二步：填写</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">邮箱地址</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">及</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">验证码</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">进行提交。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第三步：进入邮箱，点击链接地址。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">第四步：重新输入新密码提交即可找回密码。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'宋体\';\">3</span><span style=\"font-family: \'宋体\';\">、为什么我总是登录不成功？</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">请检查您的登录名、密码输入是否有误，键盘是否启用了大写锁定；</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">关闭并重新打开您的浏览器窗口，然后尝试重新登录；</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">尝试清除浏览器的缓存(IE的\"工具\"菜单，选择\"Internet&nbsp;选项\"，然后点击\"删除文件)，然后尝试重新登录。&nbsp;</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>', '255', '1', '1334988850');
INSERT INTO `ecm_article` VALUES ('24', '', '购物流程', '5', '0', '', '<p><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--></p>\r\n<div class=\"Section0\">\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong>如何购买商品？</strong></span><br /><br />在猫口袋购买商品是支持会员钱包余额支付交易的，您可放心购买，普通交易简单分以下四步（不区分境内境外）：<br /><br />第一步：拍下宝贝<br /><br />第二步：付款（此付款动作是把钱付到猫口袋）<br /><br />第三步：等待卖家发货<br /><br />第四步：确认收货（此动作是在收到货没有问题的情况下，把之前支付到猫口袋的钱打款给卖家）。</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">购物流程图：</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><img src=\"/imagesa/lct.gif\" alt=\"\" /><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">提示：放入购物车前，必须选择商品的颜色、尺码、数量。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">加入购物车</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">当您选定了一件商品后，在商品详情页点击</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">立即购买&rdquo;</span><span style=\"font-family: \'宋体\';\">，</span><span style=\"font-family: \'宋体\';\">或者</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">放入购物车</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">商品会添加到您的购物车中。这时进行确认选择操作,可选择</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">去结算</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">或</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">继续购物</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">。选择</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">去结算</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">，您将进入</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">结算流程</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">。选择</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">继续购物</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">，您可以继续挑选商品，把想要购买的商品都放入购物车中，最后一起结算。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">收货地址</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">初次购买：第一次购买，您需要填写您的收货人信息，请仔细填写，错误或不详细的收货地址会导致您收不到货或延迟您收货的时间。选择其他收货地址：如果您要选择其他的地址或收货人，请点击右上方</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">使用其他收货地址</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">，选择或添加您所需的收货地址。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">索取发票</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">您购买商品如需开具发票，请在填写订单页面</span><span style=\"font-family: \'宋体\';\">给商家留言</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">需要商品发票</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">，并填写发票抬头，之后点击</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">提交</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">。在此提醒您：没有选择</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">需要商品发票</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">并填写发票抬头，&nbsp;</span><span style=\"font-family: \'宋体\';\">商家可能</span><span style=\"font-family: \'宋体\';\">将不</span><span style=\"font-family: \'宋体\';\">会</span><span style=\"font-family: \'宋体\';\">提供商品发票。详见</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">发票制度说明</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">选择支付方式</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">选择您要采用的支付方式：在线支付。详情请见支付说明。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><br /></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">提交订单</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">提交并生成订单。如果使用</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'宋体\';\">账户</span><span style=\"font-family: \'宋体\';\">的钱包</span><span style=\"font-family: \'宋体\';\">余额</span><span style=\"font-family: \'宋体\';\">或积分</span><span style=\"font-family: \'宋体\';\">支付请在此步骤选择。</span><span style=\"font-family: \'宋体\';\">（</span><span style=\"font-family: \'宋体\';\">会员用户中心》我的钱包》积分管理，可以</span><span style=\"font-family: \'宋体\';\">输入</span><span style=\"font-family: \'宋体\';\">积分卡</span><span style=\"font-family: \'宋体\';\">领取</span><span style=\"font-family: \'宋体\';\">积分</span><span style=\"font-family: \'宋体\';\">）</span><span style=\"font-family: \'宋体\';\">即可实现帐户余额的支付。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">支付货款</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">在线支付：当您的订单生成后，会在页面下方提供支付的链接，您可以点击</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">付款</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">立刻进行网上支付，进入相应的网银</span><span style=\"font-family: \'宋体\';\">支付</span><span style=\"font-family: \'宋体\';\">平台，按照提示完成支付。</span></span><span style=\"color: #ff0099;\"><strong>（此付款动作是把钱付到猫口袋）</strong></span><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">，在支付成功后，您可以在</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">我的帐户</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">中查看您的订单信息。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">等待收货</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">当您的订单通过审核并成功支付后，您可以在</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">我的帐户</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">中查看您的订单信息，当订单信息显示</span><span style=\"font-family: \'宋体\';\">&ldquo;</span><span style=\"font-family: \'宋体\';\">已发货</span><span style=\"font-family: \'宋体\';\">&rdquo;</span><span style=\"font-family: \'宋体\';\">时，请耐心等待物流公司将商品送到您的手上。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><strong>确认收货</strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">当您</span></span>收到货品，没有问题的情况下，登陆猫口袋用户中心》我是买家》我的订单，点击确认收货，把之前支付到猫口袋的钱打款给卖家。</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0099;\"><span style=\"background-color: #ffffff;\"><strong>自动确认收货，敬请留意</strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"background-color: #ffffff;\"><span style=\"color: #000000;\">为了保证交易的正常运行，卖家已发货后，在15天时间内如果您没有确认收货，也没有申请退款，系统会默认您已收到货并确认货物没有问题，自动打款卖家。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"background-color: #ffffff;\"><span style=\"color: #000000;\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">&nbsp;<br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n</div>', '255', '1', '1334990899');
INSERT INTO `ecm_article` VALUES ('25', '', '常见问题', '6', '0', '', '<!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]-->\r\n<div class=\"Section0\">\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">如何修改订单？</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">当支付货款后，表示您此次的购买已成功，您将不能自行取消订单；如需取消订单请</span><span style=\"font-family: \'宋体\';\">及时</span><span style=\"font-family: \'Times New Roman\';\">致电</span><span style=\"font-family: \'宋体\';\">订单商家友好协商处理</span><span style=\"font-family: \'Times New Roman\';\">。</span></span></li>\r\n</ul>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">下单时可以指定送货时间吗？</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">可以，下单时您可以</span><span style=\"font-family: \'宋体\';\">要求商家注明</span><span style=\"font-family: \'Times New Roman\';\">工作日送货、休息日送货或双选，</span><span style=\"font-family: \'宋体\';\">快递</span><span style=\"font-family: \'Times New Roman\';\">节假日也可以完成配送，</span><span style=\"font-family: \'宋体\';\">猫口袋要求商家</span><span style=\"font-family: \'Times New Roman\';\">尽可能的为您提供便捷周到的服务。</span></span></li>\r\n</ul>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">收货时发现商品有问题可以拒收吗？</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">快递到达时，请您在快递人员在场的情况下本人签收并当场验货，如发现包装破损或商品问题，请您当场致电</span><span style=\"font-family: \'宋体\';\">商家电话友好协商处理</span><span style=\"font-family: \'Times New Roman\';\">，我们会帮助您妥善处理。</span></span></li>\r\n</ul>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">商品没有质量问题是否可以退换货？</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">可退换货的商品，在该商品未被使用、所有配件齐全且不影响二次销售的情况下，自签收之日起8日内可以联系我们进行退换货服务，具体政策和流程详见&ldquo;退换货流程&rdquo;。</span></span></li>\r\n</ul>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">猫口袋商城</span><span style=\"font-family: \'Times New Roman\';\">客服人员工作时间？</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">客服人员的工作时间为周一至周六，上午9:00至下午18:00，欢迎您在工作时间致电。</span></span></li>\r\n</ul>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"></span></p>\r\n</div>', '255', '1', '1334992355');
INSERT INTO `ecm_article` VALUES ('26', '', '正品保障', '7', '0', '', '<p><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">消费者保障服务之&ldquo;正品保障&rdquo;服务规则</span></span></strong></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt; text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"> </span></span><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">进驻</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">商城的商家均出售正品，商家不得销售假货及非原厂正品商品，一旦发现有出售假货及非原厂正品商品，</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">有权立即与商家终止协议，对买家进行先行赔付。&ldquo;先行赔付&rdquo;的定义：当</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">网买家与签订&ldquo;消费者保障服务协议&rdquo;的卖家（包括</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">商城商家）通过</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">猫口袋</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">进行交易后，若因该交易导致买家权益受损，且在买家直接要求卖家处理未果的情况下，买家有权在交易成功后向</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">发起针对卖家的投诉，并提出赔&nbsp;付申请。当</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">根据相关规则判定买家赔付申请成立，则有权自卖家的</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'宋体\';\">猫口袋</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">账户直接扣除相应金额款项赔付给买家。&nbsp;</span></span></p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"> </span><span style=\"font-family: \'宋体\';\"></span><span style=\"font-family: \'Times New Roman\';\"></span></span></p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>', '255', '1', '1334992624');
INSERT INTO `ecm_article` VALUES ('27', '', '在线支付方式', '5', '0', '', '<p><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--></p>\r\n<p>&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">1. 猫口袋钱包支付</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">如您选择此项支付方式，在</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">提交</span></span><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">订单后可以直接使用钱包账户的余额支付货款，您可以使用支付宝，财付通等途径对钱包进行充值。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">2.&nbsp;</span><span style=\"font-weight: bold; font-family: \'Times New Roman\';\">银行卡的开通</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">因各地银行政策不同，建议您在网上支付前拨打所在地银行电话，咨询该行可供网上支付的银行卡种类及开通手续。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">3. </span><span style=\"font-weight: bold; font-family: \'Times New Roman\';\">网上银行支付金额上限</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">目前各银行对于网上支付均有一定金额的限制，由于各银行政策不同，建议您在申请网上支付功能时向银行咨询相关事宜。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">4.&nbsp;</span><span style=\"font-weight: bold; font-family: \'Times New Roman\';\">到款时间</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">网上支付均是支付成功即到账。若由于网络故障导致您已支付成功的订单未改变订单状态，请您联系我们的客服人员为您解决。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-weight: bold; font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">5</span></strong></span></span><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">. </span></strong></span></span><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">中国银联</span></strong></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">几乎涵盖所有大中型银行发行的银行卡，覆盖率达98％，同时支持信用卡支付即时到帐方便快捷（在信用额度内）。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">6</span></span></strong></span><span style=\"font-size: small;\"><span style=\"color: #ff0066;\"><strong><span style=\"font-family: \'Times New Roman\';\">. </span></strong></span></span><span style=\"color: #ff0066;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">支付宝</span></span></strong></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">支付宝是国内领先的独立第三方支付平台，它致力于为中国电子商务提供&ldquo;简单、安全、快速&rdquo;的在线支付&nbsp;解决方案。支付宝以其在电子商务支付领域先进的技术、 风险管控能力赢得银行等合作伙伴的认同。目前已和国内工商银行、农业银行、建设银行、招商银行、上海&nbsp;浦发银行等各大商业银行以及中国邮政、VISA国际 组织等各大机构建立了战略合作，成为金融机构在网上支付领域极为信任的合作伙伴，支付宝现已有超过1亿&nbsp;的会员用户。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>', '255', '1', '1334995447');
INSERT INTO `ecm_article` VALUES ('32', '', '什么是积分？', '28', '0', '', '<p>积分是猫口袋商城的虚拟货币，能够在猫口袋这个平台用现金充值积分或者通过积分卡领取积分。积分可用于购买猫口袋商城上的各种商品；也可以兑换到更大优惠的商品，是淘实惠的最佳途径。在猫口袋，拥有积分也是资深猫友的最高象征和权力体现哦！ 目前使用积分兑换、抽奖、购买各类商品，都在猫口袋平台（http://www.maokoudai.com）进行。 <br /><br /><span style=\"color: #ff0099;\"><strong>猫口袋积分规则：</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>1. &nbsp;&nbsp; &nbsp;猫口袋积分仅限猫口袋商城内使用；<br /><br />2. &nbsp;&nbsp; &nbsp;积分可以累积，长期有效；（如若交易在使用的积分有效期之外发生退款，该部分积分不予退还）；<br /><br />3. &nbsp;&nbsp; &nbsp;10积分=1元现金；<br />&nbsp; &nbsp;&nbsp; &nbsp;在猫口袋商城购物，均可获得积分，积分返点最低10点/单，（积分返点按比例为最低商品售价的0.5%，虚拟类商品最低积分返点比例为0.1% ）；<br />&nbsp; &nbsp;&nbsp; &nbsp;例如某商品积分派送比例为0.5%，不同售价情况下送出积分情况如下：<br />&nbsp; &nbsp;&nbsp; &nbsp;<br />&nbsp; &nbsp;&nbsp; &nbsp;商品售价 &nbsp;&nbsp; &nbsp;买家获得积分<br />&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; 200&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 100积分<br />&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; 198&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; 99积分<br />&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; 202&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; 101积分<br /><br />4. &nbsp;&nbsp; &nbsp;商家可以设置&ldquo;多倍返积分商品&rdquo;，用最低比例的倍数作为自设的积分比例，最高为50%。<br />&nbsp; &nbsp;&nbsp; &nbsp;例如某商品售价100元，若商家设置积分派送比例为5%，则买家可获得500积分;<br /><br />5. &nbsp;&nbsp; &nbsp;积分的数值精确到个位（小数点后全部舍弃，不进行四舍五入），<br />&nbsp; &nbsp;&nbsp; &nbsp;例如：原价109元的商品，返54（109*0.5=54.5）个积分；<br /><br />6. &nbsp;&nbsp; &nbsp;买家在付款时，每笔订单可使用的积分额度无上限，买家在完成该笔交易后（猫口袋网站显示该交易状态为&ldquo;交易成功&rdquo;），才能得到此次交易的相应积分；<br /><br />7. &nbsp;&nbsp; &nbsp;买家在使用积分时，优先消耗旧积分（如买家积分由去年3月份和今年5月份共同累积，则优先消耗去年3月份的积分）；<br /><br />8. &nbsp;&nbsp; &nbsp;积分不能兑现，不可转让。</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: #ff0099;\"><strong>常见FAQ</strong></span><br /><span style=\"color: #ff0099;\"><br /><strong>Q1. 积分能兑现吗？</strong></span><br />积分不能兑现，不能转让，只能在猫口袋商城购物中抵扣现金，或者在积分频道兑换商品。<br /><span style=\"color: #ff0099;\"><strong><br />Q2. 为什么我的积分在没有使用的情况下变少了？</strong></span><br />积分是可以累积的，但过期的部分会被系统自动扣除。积分的有效期至少为1年，即从获得开始至次年年底，逾期自动作废。<br /><br /><span style=\"color: #ff0099;\"><strong>Q3. 如何查看我的积分？</strong></span><br />登录猫口袋商城&mdash;用户中心页面，登录成功后即可看到您的当前积分，或者在&ldquo;用户中心&mdash;我的钱包&mdash;积分管理&rdquo;中查看。<br /><br /><span style=\"color: #ff0099;\"><strong>Q4..积分可以抵运费吗？</strong></span><br />积分可以抵运费，运费可以在付款页面同商品费用一同支付。<br /><span style=\"color: #ff0099;\"><strong><br />Q5. 如果在猫口袋商城申请了售后服务（交易成功后申请退款），我得到的积分怎么办?</strong></span><br />买家申请了售后服务并要求退回相应的货款，在当初交易成功时，买家所得到积分对应的货款，是需要退回卖家的。猫口袋工作人员在操作时，会从货款中减掉积分的款项。<br />例如：交易金额为200元，买家当时获得了23个积分，即0.23元，我们在操作退款时，是退回买家：200-0.23=199.77元，而不是 200元。买家在当时得到的0.23元，是可以用于下一次交易中的。<br /><br /><span style=\"color: #ff0099;\"><strong>Q6. 我参加了商家&ldquo;团购&ldquo;活动，为什么没有送积分？</strong></span><br />商家的&ldquo;团购&ldquo;活动，同一款商品，看最终成交的交易金额（扣除运费）是否满足活动要求，如满足，商家便会为您核发积分，买家可同商家联系，协调发放积分。<br /><br /><br /><br /><br /></p>', '255', '1', '1335001270');
INSERT INTO `ecm_article` VALUES ('28', '', '猫口袋账户说明', '4', '0', '', '<div class=\"user-right\" style=\"width: 783px;\">\r\n<div class=\"Recommend-title margig15\"><strong></strong></div>\r\n<div class=\"helptitle\"><span style=\"color: #ff0066;\"><strong>什么是猫口袋账户？ </strong></span></div>\r\n<div class=\"helptitle\"><br /></div>\r\n<div class=\"info-help\">\r\n<p>猫口袋账户是猫口袋商城会员在猫口袋（www.maokoudai.com）网站拥有的用于存放由于退货、奖励、补款等产生的虚拟资产和现金的账户，</p>\r\n<p>可用余额支付商品货款。</p>\r\n<p>&nbsp;</p>\r\n<p>猫口袋账户可以用于在猫口袋商城上选择，浏览咨询，购买商品，以及货物确认和评价，申诉。</p>\r\n</div>\r\n</div>', '255', '1', '1334995800');
INSERT INTO `ecm_article` VALUES ('29', '', '适用范围', '26', '0', '', '<p>1、包括猫口袋网站处理用户在登录网站和服务器时留下的个人身份识别信息，以及其他相关身份识别的信息。<br />包括猫口袋网站会员客户与签约商家两类。</p>\r\n<p><br />2、隐私权政策不适用于非猫口袋网站所属机构及非猫口袋网站会员。</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '255', '1', '1334998234');
INSERT INTO `ecm_article` VALUES ('30', '', '隐私信息收集与保护', '26', '0', '', '<!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]-->\r\n<div class=\"Section0\">\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"color: #ff0099; font-weight: bold; font-family: \'Times New Roman\';\">隐私信息收集与保护</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">1．在您注册本网站会员时、使用本网站产品或服务、访问本网站网页，本网站会收集你的个人身份识别资料。本网站也会收集来自商业伙伴的身份识别资料。只要你在本网站成功注册并登录服务器，我们将可以识别你。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">2．您可以随时修改您的个人信息和密码，请谨慎保护您的密码，防止密码被盗，个人信息外泄。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">3．本网站会将这些资料用于：</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （1）网站用户的信息统计工作；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （2）优化为您提供的服务及体验工作；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （3）您所同意的其他用途。</span><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\">4．</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">非常重视对用户隐私权的保护，</span><span style=\"font-family: \'宋体\';\">猫口袋</span><span style=\"font-family: \'Times New Roman\';\">在此郑重承诺：不会在未获得用户许可的情况下擅自将用户的个人资料信息出租或出售给任何第三方。除以下免责条款外：</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;您同意公开你的个人资料，享受为您提供的产品和服务；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;本站需要听从法庭传票、法律命令或遵循法律程序；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;本站发现您违反了本站服务条款或本站其它使用规定。</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\">5、出现下列情况时本网站不承担任何责任：</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （1）您同意让第三方共享资料；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （2）您虽未同意，但由于您将用户密码告知他人或与他人共享注册帐户，由此导致的任何个人资料泄露。</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （3）只有透露您的个人资料，才能提供您所要求的产品和服务；</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （4）任何由于不可抗力、黑客政击、计算机病毒侵入或发作、因政府管制而造成的暂时性关闭等影响网络正常经营之不可抗力而造成的个人资料泄露、丢失、被盗用或被篡改等。&nbsp; </span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （5）由于与本网站链接的其它网站所造成之个人资料或相关内容泄露及由此而导致的任何法律争议和后果。</span><span style=\"font-family: \'Times New Roman\';\"><br /></span><span style=\"font-family: \'Times New Roman\';\"> （6）我们发现您违反了本网站服务条款或其他产品及服务的使用规定。</span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"><br /></span></span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 0pt; margin-top: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: \'Times New Roman\';\"></span></span></p>\r\n</div>', '255', '1', '1334998627');
INSERT INTO `ecm_article` VALUES ('31', '', '其他隐私保护说明', '26', '0', '', '<p>1．本网站关于隐私保护条款的解释权、修改及更新权均属于猫口袋所有。</p>\r\n<p><br />2．随着我们服务范围的扩展、服务技术的提高、服务方式的改进，我们会不断更新相关隐私政策！敬请关注！</p>\r\n<p><br />3．如有疑问，欢迎您联系猫口袋客服中心进行咨询。</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '255', '1', '1334998781');
INSERT INTO `ecm_article` VALUES ('33', '', '商家进驻条件', '29', '0', '', '<p><strong><span style=\"color: #ff0099;\">商家进驻报名条件：</span></strong></p>\r\n<p><strong><br />淘宝店：</strong>店铺信用&ge;3钻，开店时间大于3个月，当月，满足以上任意一条即可申请在猫口袋开店；<br /><br /><strong>拍拍/天猫/拍拍/有货/MSN购物/阿里巴巴：</strong>开店时间大于3个月，店铺商品数量超过20件；<br /><br /><strong>黑名单规则：</strong>炒作信用，黑名单卖家永久禁止报名。<br /><br /><strong><span style=\"color: #ff0099;\">商家进驻流程：</span></strong></p>', '255', '1', '1335002554');
INSERT INTO `ecm_article` VALUES ('34', '', '特殊类目商品发布要求', '29', '0', '', '<p>猫口袋特殊商品需要满足特定的条件，由官方审核后才能上架。主要包含有：</p>\r\n<p>&nbsp;</p>\r\n<p>1、优惠价格5元以下的商品，5元和5元以下货值的商品（不论商品类目），发布商品的商家账户需要最少30颗猫粮；</p>\r\n<p>&nbsp;</p>\r\n<p>2、发布在特色市场类目的商品；</p>\r\n<p>&nbsp;</p>\r\n<p>猫口袋特色市场包括<strong>&ldquo;10元特价、3折封顶、热销精品、积分专享&rdquo;</strong>4个板块；发布于特色市场的商品需要符合特定条件，并通过猫口袋审核后，方可上架。商家在猫口袋可以迅速吸引大量客源流量；<strong><br /></strong></p>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>10元特价：</strong>商家在猫口袋发布原价超过10元，猫口袋优惠价格低于10元的商品。<strong>特别说明：</strong>本板块的商品默认排序规则按原价与优惠差价多少从多到少依次排列，价差越大排名越靠前；<strong><br /></strong></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>&nbsp; 3折封顶：</strong>所发布商品必须有销售记录，商家在猫口袋发布优惠价格必须为原价的3折。</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>热销精品：</strong>由猫口袋商城每周（7天）自动筛选一次，将各类目销量排行最高的商品自动加入热销精品类目，商品进入热销精品后在一周（7天）后自动返回原类目；</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>积分专享：</strong>商家发布商品<strong>积分加钱购</strong>，所发布商品必须有销售记录，优惠价必须为原价8折或8折以下。<strong>特别说明：</strong>积分专享商品成交订单不论是否新老客交易户均不消耗<a href=\"http://www.maokoudai.com/index.php?app=article&amp;act=view&amp;article_id=18\" target=\"_blank\"><strong>商家猫粮</strong></a>；</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>注：</strong>同一件商品仅限加入一个类目，不能重复加入特色市场不同的板块。</li>\r\n</ul>', '255', '1', '1336225530');
INSERT INTO `ecm_article` VALUES ('35', '', '客源佣金分成', '29', '0', '', '<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">客户在任何B2C/C2C平台与商家达成交易以后，商家可以向客户发放\"礼品卡\"后，当客户在猫口袋使用礼品卡兑入积分，此客户就算作商家共享的一个客户资源。</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">商家每共享一个客户资源，除了赚取猫粮以外，还可以获得这些客户在猫口袋商城每次消费金额8%佣金分成。</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\"><strong>商家在</strong></span></span><span class=\"font\"><span class=\"font\"><strong>猫口袋只要共享自己的客户，就可以一劳永逸坐享佣金分成。随着商家共享客户资源积累越来越多，商家可以赚取更多的规模化收益；</strong></span></span></li>\r\n</ul>\r\n<p><strong><br /></strong></p>\r\n<ul>\r\n<li><strong>注册商家的客源查看路径：</strong>商家用户中心&nbsp;》 我是卖家 》 客源管理&nbsp;》 客户资源&nbsp;》 客源收益</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '255', '1', '1336251253');
INSERT INTO `ecm_article` VALUES ('36', '', '猫の礼品卡', '28', '0', '', '<p><strong><br /></strong></p>\r\n<ul>\r\n<li><strong>猫の礼品卡</strong>，是由商家自己在猫口袋网站生成的一种具有一定积分面值的虚拟卡。礼品卡只有卡号没有密码。猫口袋的任何会员使用<strong>礼品卡</strong>均可以直接向自己账户兑入积分。</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li>除<strong>商家</strong>不可以使用自己生成的礼品卡外。猫口袋会员使用其他礼品卡使用没有限制；</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">商家向<strong>自家淘宝/拍拍店</strong>已完成交易的客户发送猫口袋礼品卡， 客户使用礼品卡在猫口袋网站领取积分（10积分=1元）。积分可以直接用于猫口袋商城购物消费，并且使用无任何限制，可以购买Q币、冲话费、抵货款、抵运费、兑换礼品&hellip;&hellip; <br /></span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">商家向客户赠送<strong>\"猫の礼品卡\"</strong>是比\"打折促销、赠品、优惠卡、抵价券，淘客返利，竞价推广&hellip;&hellip;\"更高效的店铺营销策略。让商家获得更好的评价和口碑，带来更多客源订单！</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">对客户而言，<strong>\"猫の礼品卡\"</strong>比直接送礼品更受欢迎，因为每个人的需求和喜 好都不一样。使用礼品卡的好处在于不必再与商家为挑选一款自己想要礼物去讨价还价说上半天，无需再与商家纠结赠品的品质问题，也不会再为收到\"鸡肋礼品\" 而郁闷。猫口袋礼品卡让客户自己拥有了选择权，可以自由选择喜欢的东西。另外，还可以长期把积分积攒在一起，用大量积分去换购更高货值商品。</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">商家生成礼品卡所包含的积分来源，由商家按每</span></span><span class=\"font\"><span class=\"font\">10积分=1元向猫口袋网站购买；</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><span class=\"font\"><span class=\"font\">商家可自由设定每张礼品卡的积分数量，</span></span><span class=\"font\"><span class=\"font\">礼品卡号码由猫口袋网站随机生成。</span></span><span class=\"font\"><span class=\"font\">礼品卡不能重复使用，每张卡号只能使用一次。</span></span><span class=\"font\"><span class=\"font\">礼品卡发布后15日之内不领取积分将失效，礼品卡失效后所含积分自动返还发卡商家；</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>礼品卡兑入路径：</strong>用户中心&nbsp;》 我的钱包&nbsp;》&nbsp;积分管理&nbsp;》 领取积分 》&nbsp;输入商家给您的礼品卡号码，点击<strong>立即领取</strong>，您的账户立即会马上兑入这个礼品卡所包含的积分。</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li><strong>礼品卡生成路径：</strong>商家用户中心 》 我的钱包&nbsp;》&nbsp;积分管理&nbsp;》 生成礼品卡 》 商家根据自己的需求设置：卡号前缀，卡数量， 积分数量，最后输入自己的购物支付密码（非登陆密码）点击<strong>立即生成</strong>即可。</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li>当商家生成礼品卡后，直接点击卡号后面的&ldquo;复制&rdquo;按钮，可以复制包含<strong>本张礼品卡号码</strong>和<strong>猫口袋网址</strong>的兑入提示信息。商家直接通过向客户的邮箱、旺旺，QQ等渠道发放礼品卡。当礼品卡被使用后，商家可以在礼品卡生成页面看到。否则，<span class=\"font\"><span class=\"font\">自卡发布15日之内没有使用领取积分将失效，礼品卡失效后所含积分自动返还发卡商家；</span></span></li>\r\n</ul>\r\n<p>&nbsp;</p>', '255', '1', '1336251417');
INSERT INTO `ecm_article` VALUES ('37', '', '新商家成长计划', '29', '0', '', '<!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]-->\r\n<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \">&nbsp;</p>\r\n<p class=\"p0\" style=\"margin-bottom:0pt; margin-top:0pt; \"><span style=\"mso-spacerun:\'yes\'; color:#ff0000; font-weight:bold; font-size:24.0000pt; font-family:\'微软雅黑\'; \">新商家成长计划</span><span style=\"font-size: medium;\"><span style=\"color: #ff0000; font-weight: bold; font-family: \'微软雅黑\';\">，即猫口袋特色市场。猫口袋是卖家</span></span><span style=\"mso-spacerun:\'yes\'; color:#ff0000; font-weight:bold; font-size:18.0000pt; font-family:\'微软雅黑\'; \">共享客户资源</span><span style=\"font-size: medium;\"><span style=\"color: #ff0000; font-weight: bold; font-family: \'微软雅黑\';\">的平台，在这里，不管你是几星级的卖家，大家都站在同一水平线上，谁跳的高，看的远，都凭各家的本事！免费的巨大流量，你还在眼睁睁看着它流走吗？</span><span style=\"color: #ff0000; font-weight: bold; font-family: \'微软雅黑\';\">猫口袋</span><span style=\"color: #ff0000; font-weight: bold; font-family: \'微软雅黑\';\">告诉你</span></span><span class=\"15\" style=\"mso-spacerun:\'yes\'; color:#ff0000; font-weight:bold; font-style:normal; font-size:18.0000pt; font-family:\'微软雅黑\'; \">电商全盘运营计划</span><span style=\"mso-spacerun:\'yes\'; color:#ff0000; font-weight:bold; font-size:9.0000pt; font-family:\'微软雅黑\'; \">，</span><span style=\"mso-spacerun:\'yes\'; color:#ff0000; font-weight:bold; font-size:18.0000pt; font-family:\'微软雅黑\'; \">教你如何玩赚超高转化率，尽享免费的流量！</span><span style=\"font-size: medium;\"><span style=\"color: #ff0000; font-weight: bold; font-family: \'微软雅黑\';\">多位</span><span style=